import React, { useEffect } from "react";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";
import { GLTFLoader } from "three/addons/loaders/GLTFLoader.js";
import { createNoise3D } from "simplex-noise";

const ThreeBackground = () => {
    const noise = createNoise3D();

    useEffect(() => {
        //getting audio context
        const context = new AudioContext();
        const src = context.createMediaElementSource(audio);
        const analyser = context.createAnalyser();
        src.connect(analyser);
        analyser.connect(context.destination);
        analyser.fftSize = 512;
        const bufferLength = analyser.frequencyBinCount;
        var dataArray = new Uint8Array(bufferLength);

        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(
            50,
            window.innerWidth / window.innerHeight,
            1,
            1000
        );
        camera.position.z = 96;

        const canvas = document.getElementById("three-bg-canvas");

        const renderer = new THREE.WebGLRenderer({
            canvas,
            antialias: true,
        });

        renderer.setSize(window.innerWidth, window.innerHeight);

        const container = document.getElementById("three-bg-container");

        container.appendChild(renderer.domElement);

        var loader = new GLTFLoader();

        loader.load("/glb/sui.glb", function (gltf) {
            const albumModal = gltf.scene;
            albumModal.scale.set(0.1, 0.1, 0.1);
            albumModal.position.set(-20, -24, -18);
            scene.add(albumModal);
        });

        // const loader = new THREE.TextureLoader();
        // const texture = loader.load(img);

        // const geometry = new THREE.PlaneGeometry(20, 20); // ensure correct aspect ratio
        // const material = new THREE.MeshBasicMaterial({ map: texture });

        // const albumPlane = new THREE.Mesh(geometry, material);
        // scene.add(albumPlane);

        const icosahedronGeometry = new THREE.IcosahedronGeometry(20, 5);
        const lambertMaterial = new THREE.MeshLambertMaterial({
            color: 0xffffff,
            wireframe: true,
        });

        const musicCube = new THREE.Mesh(icosahedronGeometry, lambertMaterial);
        musicCube.position.set(0, 0, 0);
        scene.add(musicCube);

        const ambientLight = new THREE.AmbientLight(0xffffff, 0.8);
        ambientLight.castShadow = true;
        scene.add(ambientLight);

        const spotLight = new THREE.SpotLight(0xffffff, 1);
        spotLight.castShadow = true;
        scene.add(spotLight);

        const controller = new OrbitControls(camera, renderer.domElement);

        const animate = () => {
            controller.update();
            renderer.render(scene, camera);
            window.requestAnimationFrame(animate);
        };
        animate();
    }, []);

    const makeRoughBall = (mesh, bassFr, treFr) => {
        mesh.geometry.vertices.forEach(function (vertex, i) {
            var offset = mesh.geometry.parameters.radius;
            var amp = 7;
            var time = window.performance.now();
            vertex.normalize();
            var rf = 0.00001;
            var distance =
                offset +
                bassFr +
                noise(
                    vertex.x + time * rf * 7,
                    vertex.y + time * rf * 8,
                    vertex.z + time * rf * 9
                ) *
                    amp *
                    treFr;
            vertex.multiplyScalar(distance);
        });
        mesh.geometry.verticesNeedUpdate = true;
        mesh.geometry.normalsNeedUpdate = true;
        mesh.geometry.computeVertexNormals();
        mesh.geometry.computeFaceNormals();
    };

    return (
        <div id="three-bg-container">
            <canvas id="three-bg-canvas" />
            <audio
                src="/music/freestyle.wav"
                controls
                style={{ position: "absolute", bottom: "0" }}
            />
        </div>
    );
};

export default ThreeBackground;
